// Agrupa por e-mail da coluna 'invite' e conta quantas vezes aparece
const contagem = {};

for (const item of items) {
  let email = item.json.invite;

  if (!email || email.trim() === '') {
    email = 'Oculto';
  }

  if (contagem[email]) {
    contagem[email]++;
  } else {
    contagem[email] = 1;
  }
}

// Converte para array, ordena e monta mensagem de ranking
const medalhas = ['🥇', '🥈', '🥉'];

const ranking = Object.entries(contagem)
  .map(([email, total]) => ({ email, total }))
  .sort((a, b) => b.total - a.total)
  .map((entry, index) => {
    const posicao = index < 3 ? medalhas[index] : `${index + 1}º`;
    return `${posicao} ${entry.email} — ${entry.total} cadastros`;
  })
  .join('\n');

// Retorna uma única saída com a mensagem pronta
return [
  {
    json: {
      mensagem: ranking
    }
  }
];

