const contagem = {};

for (const item of items) {
  let email = item.json.invite;

  if (!email || email.trim() === '') {
    email = 'CADASTROS DA CASA';
  }

  if (contagem[email]) {
    contagem[email]++;
  } else {
    contagem[email] = 1;
  }
}

// Converte para array, ordena e adiciona ranking com emojis
const medalhas = ['🥇', '🥈', '🥉'];

const resultado = Object.entries(contagem)
  .map(([email, total]) => ({ email, total }))
  .sort((a, b) => b.total - a.total)
  .map((entry, index) => {
    const posicao = index < 3 ? medalhas[index] : `${index + 1}`;
    return {
      json: {
        posicao,
        email: entry.email,
        total: entry.total
      }
    };
  });

return resultado;

const hoje = new Date();
const dataFormatada = hoje.toLocaleDateString('pt-BR').split('/').reverse().join('-'); // "2025-06-12"

const [ano, mes, dia] = dataFormatada.split('-');
const final = `${dia}-${mes}-${ano}`; // "12-06-2025"

return [
  {
    json: {
      hoje: final
    }
  }
];
