// Pega o primeiro item de entrada
const item = items[0];
const json = item.json;

// Descobre onde est√° o "entry": dentro de body ou na raiz
let body;

if (json.body && json.body.entry) {
	// Formato: { body: { entry: [...] } }
	body = json.body;
} else if (json.entry) {
	// Formato: { entry: [...] }
	body = json;
} else {
	// N√£o achou estrutura esperada
	return [
		{
			json: {
				error: 'Estrutura inesperada. N√£o encontrei body.entry nem entry direto.',
				receivedKeys: Object.keys(json),
			},
		},
	];
}

// Agora segue o caminho padr√£o da Meta
const entry = body.entry?.[0];
const change = entry?.changes?.[0];
const value = change?.value;

// Tenta pegar do contacts[0].wa_id, se n√£o tiver cai pro messages[0].from
let waId = value?.contacts?.[0]?.wa_id || value?.messages?.[0]?.from;

if (!waId) {
	return [
		{
			json: {
				error: 'wa_id n√£o encontrado em contacts[0].wa_id nem em messages[0].from',
				valueKeys: value ? Object.keys(value) : null,
			},
		},
	];
}

// Garante que √© s√≥ d√≠gito
waId = String(waId).replace(/\D/g, '');

if (!waId) {
	return [
		{
			json: {
				error: 'wa_id vazio ap√≥s limpar caracteres',
			},
		},
	];
}

// DDI = dois primeiros d√≠gitos
const countryCode = waId.substring(0, 2);

// Brasil = 55
const isBrazilian = countryCode === '55';

// Code node precisa devolver um ARRAY de items
return [
	{
		json: {
			wa_id: waId,
			countryCode,
			isBrazilian,
		},
	},
];

// Obt√©m a data do JSON
