// Converte a string para um objeto Date
const dataLead = new Date(dataHora);

// Obt√©m a data e hora atuais
const agora = new Date();

// Calcula a diferen√ßa em milissegundos e converte para horas
const diferencaHoras = (agora - dataLead) / (1000 * 60 * 60);

