conditions.leftValue:
={{ 
  (() => {
    // Converte "HH:mm" em minutos desde 00:00
    const toMinutes = (t) => {
      const [h = 0, m = 0] = String(t).trim().split(':').map(Number);
      return h * 60 + m;
    };

    // Hora atual em minutos
    const agora = new Date();
    const agoraMin = agora.getHours() * 60 + agora.getMinutes();

    // Hor√°rio configurado no Webhook2
    const startMin = toMinutes($item("0").$node["DISPARAR MENSAGENS"].json["body"]["settings"]["startTime"]); // ex. "08:00" ou "8:27"
    const endMin   = toMinutes($item("0").$node["DISPARAR MENSAGENS"].json["body"]["settings"]["endTime"]);   // ex. "18:00"

    // Retorna true se agora estiver no intervalo [start, end]
    return agoraMin >= startMin && agoraMin <= endMin;
  })() 
}}

parameters.dateTime:
={{ (() => {
     /* dias permitidos (1-seg ‚Ä¶ 7-dom) */
     const dias = $item("0").$node["DISPARAR MENSAGENS"].json.body.settings.selectedDays || [];

     /* dia de hoje nessa mesma conven√ß√£o */
     const now  = new Date();
     const hoje = now.getDay() === 0 ? 7 : now.getDay();

     /* diferen√ßa m√≠nima (0-6) at√© o pr√≥ximo dia permitido */
     let diff = 7;
     for (const d of dias) {
       const delta = (d - hoje + 7) % 7;
       if (delta < diff) diff = delta;
     }

     /* data-alvo: hoje + diff dias, hora 00:00 */
     const target = new Date(now);
     target.setDate(now.getDate() + diff);
     target.setHours(0, 0, 0, 0);

     /* formata YYYY-MM-DDT00:00:00  (sem offset) */
     const pad = n => String(n).padStart(2, '0');
     return `${target.getFullYear()}-${pad(target.getMonth()+1)}-${pad(target.getDate())}T00:00:00`;
   })() }}
