const date = new Date();
const options = {
    timeZone: 'America/Sao_Paulo',
    year: 'numeric',
    month: '2-digit',
    day: '2-digit',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit'
};

const formattedDate = new Intl.DateTimeFormat('pt-BR', options).format(date);
return formattedDate;
