// Definir variáveis manualmente (ou através de outros nós antes de chegar aqui)
const textoCompleto = items[0].json.mensagem;  // Texto completo da mensagem
const numero1 = items[0].json.numero1;  // Primeiro número
const numero2 = items[0].json.numero2;  // Segundo número
const instancia1 = items[0].json.instancia1;  // Primeira instância
const instancia2 = items[0].json.instancia2;  // Segunda instância

// Dividir o texto em mensagens separadas por '\n'
const mensagensSeparadas = textoCompleto.split('\n');

let alternador = true;  // Para alternar entre numero1 e numero2
let contador = 0;  // Inicializa o contador

// Retorna as mensagens com alternância entre os números e instâncias
return mensagensSeparadas.map(mensagem => {
  const numeroAtual = alternador ? numero1 : numero2;  // Alterna entre os números
  const instanciaAtual = alternador ? instancia2 : instancia1;  // Alterna a instância para enviar a mensagem

  alternador = !alternador;  // Inverte o alternador para a próxima mensagem

  // Incrementa o contador e reseta para 1 após chegar a 20
  contador = (contador % 20) + 1;

  return {
    json: {
      numero: numeroAtual,  // Define qual número enviará a mensagem
      instancia: instanciaAtual,  // Define a instância para onde a mensagem será enviada
      mensagem: mensagem.trim(),  // Remove espaços em branco extras da mensagem
      contador: contador  // Contador que vai de 1 até 20 e depois reinicia
    }
  };
});
