// Obtenha os dados da variável diretamente
const data = $('Pegando mensagens do grupo').item.json.messages.records || [];

// Obtenha a data de hoje em formato UTC (sem horário)
const today = new Date();
today.setHours(0, 0, 0, 0);

// Filtrar mensagens cuja data seja igual à de hoje
const filteredMessages = data.filter(record => {
    const messageDate = new Date(record.messageTimestamp * 1000); // Convertendo timestamp para milissegundos
    messageDate.setHours(0, 0, 0, 0); // Removendo a hora para comparar só o dia

    return messageDate.getTime() === today.getTime();
});

// Contabilizar as mensagens por participante
const participantCount = {};

// Contagem das mensagens por participante
filteredMessages.forEach(record => {
    let participant = record.key?.participant; // Verifique se a chave existe
    const pushName = record.pushName || "Nome não identificado"; // Captura o pushName

    if (participant) {
        // Remove o sufixo @s.whatsapp.net
        participant = participant.replace('@s.whatsapp.net', '');

        if (!participantCount[participant]) {
            participantCount[participant] = {
                count: 0,
                pushName: pushName // Armazena o pushName
            };
        }
        participantCount[participant].count++;
    }
});

// Ordenar participantes pela quantidade de mensagens (do maior para o menor)
const sortedParticipants = Object.entries(participantCount)
    .sort((a, b) => b[1].count - a[1].count) // Ordena pela quantidade de mensagens
    .slice(0, 3); // Pega os 3 primeiros

// Gerar a mensagem única com os três melhores participantes
const message = sortedParticipants.map((participant, index) => {
    const medal = ["🥇", "🥈", "🥉"][index]; // Seleciona o emoji da medalha com base no índice
    const pushName = `*${participant[1].pushName}*`; // Adiciona asteriscos ao pushName
    const count = participant[1].count; // Pega a contagem de mensagens
    return `${medal} ${pushName}\n> Com ${count} mensagens`;
}).join('\n\n'); // Junta as mensagens com quebras de linha

// Retornar a mensagem única como resultado
return [
    {
        json: {
            mensagem: message
        }
    }
];

